import {KnowledgeContent} from "../../../components/layouts/Knowledge/KnowledgeLayout/KnowledgeContent";
import {MainLayout} from "../../Navbar/MainLayout";
import {useNavigate} from "react-router-dom";
import {useEffect, useState} from "react";
import {client} from "../../../utils/sanity/client";

export const KnowledgeLayout: React.FC<{}> = props => {
    const navigate = useNavigate()

    const [categories, setCategories] = useState(null);

    const [cards_, setCards_] = useState([
        {
            cover: 'https://image.shutterstock.com/image-photo/image-260nw-634574354.jpg',
            title: 'Backend',
            text: 'База знаний для бека',
            link: '1'
        },
        {
            cover: 'https://avatars.githubusercontent.com/u/5490541?s=400&amp;v=4',
            title: 'Frontend',
            text: 'База знаний для frontend',
            link: '2'
        },
        {
            cover: 'https://studprosvet.ru/upload/005/u533/d/5/to-s-chego-vse-i-nachinaetsja-photo-normal.jpg',
            title: 'Mobile',
            text: 'База знаний для mobile',
            link: '3'
        },
    ])

    useEffect(() => {
        client.fetch(
            `*[_type == "organization"]{
      knowledge {
        cover {

        }
      }
    }`)
            .then((data:any) => {
                setCards_(data[0])
                console.log(data[0])
            })
            .catch(console.error);
    }, []);



    return (
        <>
            <MainLayout>
                <div className={'ml-32 bg-secondary w-screen h-full min-h-screen'}>
                    <div className={'flex pt-20 mb-5 ml-10 mr-10 items-center'}>
                        <h3 className={'font-montserratBold text-3xl'}>База знаний</h3>
                        <button
                            onClick={() => navigate('/knowledge/create')}
                            className={'w-48 font-montserratRegular px-5 py-3 rounded-2xl hover:bg-white bg-black ml-auto hover:text-black transition-all duration-300 text-white'}>Создать
                        </button>
                    </div>

                    <KnowledgeContent cards={cards_}/>
                </div>
            </MainLayout>


        </>
    );
};
