export const KnowledgeItemMedia: React.FC<{ media?: string[], cover?: string, editable?: boolean }> = props => {
    return (
        <>
            <div className={'flex mt-5'}>
                <div className={'bg-white w-auto rounded-2xl p-5 mr-5'}>
                    <label className={'font-montserratSemibold'} htmlFor={"media"}>
                        Медиа
                    </label>
                    <br/>
                    <input className={'w-auto text-sm font-montserratRegular bg-black'} type="file" id={'media'} multiple={true} />
                </div>

                <label className={'bg-white rounded-2xl p-5'}>
                    <h3>Обложка</h3>
                    <input type="file" name="cover" multiple={false} hidden={true}/>
                </label>
            </div>

        </>
    );
};
