import {MainLayout} from "../../Navbar/MainLayout";
import {MemberItem} from "../../../components/layouts/Team/MemberDetail/MemberItem";
import {MemberInfo} from "../../../components/layouts/Team/MemberDetail/MemberInfo";
import {MemberType, modeAcces, roleType} from "../../../utils/types/types";
import {useState} from "react";

export const MemberDetail: React.FC<{ mode: modeAcces }> = ({mode}) => {

    const id_ = Number(window.location.pathname.split('/')[2])

    const [card_, setCard] = useState<MemberType>({
        img: '',
        name: '',
        bio: '',
        role: roleType.user,
        employee: '',
        link: '',
        phone:'',
        email:'',
        vk:'',
        tg:'',
        insta:''
    })

    const cards_ = [
        {
            img: 'https://begeton.com/files/users-persons_thumb/121/5/30/stGHlM7lakOOgET27IANp6AOQPwaV0j8.jpeg',
            name: 'Alex',
            bio: 'База знаний для бека',
            role: roleType.user,
            employee: 'Frontend',
            link: '1'
        },
        {
            img: 'https://www.belarosso.ru/imgc/2022/106b/319920/futbolka-infinity-31200510007-belyy-4.jpg',
            name: 'Gordon',
            bio: 'База знаний для frontend',
            role: roleType.mentor,
            employee: 'Backend',
            link: '2'
        },
        {
            img: 'https://a.lmcdn.ru/pi/img600x866/M/P/MP002XW04SZK_13074051_5_v1.jpeg',
            name: 'Luice',
            bio: 'База знаний для mobile',
            role: roleType.admin,
            employee: 'Frontend',
            link: '3'
        }
    ]

    setCard(cards_[id_-1])

    return (
        <>
            <MainLayout>
                <div className={'ml-32 bg-secondary flex w-screen h-full min-h-screen'}>
                    <MemberItem card={card_}/>
                    <MemberInfo />
                </div>
            </MainLayout>
        </>
    );
};
