import {postDetailType} from "../../../../utils/types/types";

type Props = {
    postDetail: postDetailType;
};

export const PostDetailContent: React.FC<Props> = ({postDetail}) => {
    return (
        <>
            <div className={'w-64 h-auto bg-white grid'}>
                <div className={'flex items-center'}>
                    <img className={'w-16 h-16 rounded-2xl'} src={postDetail.author.avatar}/>
                    <div className={'grid'}>
                        <h3 className={'font-montserratSemibold text-black text-lg'}>{postDetail.author.name}</h3>
                        <p className={'font-montserratRegular text-secondary text-sm'}>{postDetail.date}</p>
                    </div>
                    <button className={'ml-auto'}>more</button>
                </div>
                <div>
                    {postDetail.images?.map((img, index) =>
                        <img src={img} />
                    )}
                </div>

                <p>{postDetail.text}</p>
                <div>
                    <button>like</button>
                    <button>comment</button>
                    <button>repost</button>
                    <button>share</button>
                </div>


            </div>
        </>
    );
};
