import {AuthorType} from "../../../../utils/types/types";
import react from "react";

type Props = {
    author: AuthorType;
}
export const CardAuthor: React.FC<Props> = ({author}) => {
    return (
        <>
            <div className={'bg-white w-auto h-fit grid mt-10 py-5 rounded-2xl justify-items-center '}>
                <img className={'w-32 h-32 rounded-2xl'} src={author.avatar}/>
                <div className={'flex mt-3'}>
                    <h3 className={'font-montserratBold text-lg'}>{author.name}</h3>
                    <img className={author.isVerified ? 'visible' : 'none'}/>
                </div>
                <p className={'font-montserratSemibold mt-3'}>{author.commonSubs} общих связей</p>
                <p className={'font-montserratRegular mt-3'}>{author.bio}</p>
                <button className={'px-5 py-3 rounded-2xl font-montserratRegular mt-3'}>{author.subscribed ? 'Отписаться' : 'Подписаться'}</button>
            </div>
        </>
    );
};
